import { useRef } from "react";
import { useGlobalContext } from "./context";
export const Component = ({ name }) => {
  const { edit, addTask, remove, state, editDone, changePlace, removeColoumn } =
    useGlobalContext();
  const inputRef = useRef(null);
  return (
    <td>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          addTask({ text: e.target.inputref.value, type: name });
        }}
      >
        <input type="text" name="inputref" ref={inputRef} />
        <button type="submit">Add</button>
        {state.text.map((item) => item.type === name).lenghth != "" ? (
          <button type="button" onClick={() => removeColoumn(name)}>
            Clear
          </button>
        ) : (
          ""
        )}
      </form>
      <br />

      {state.text.map((task) => {
        const { type, content, id, isEditing } = task;
        if (type === name) {
          return (
            <div key={id}>
              {isEditing ? (
                <form
                  onSubmit={(e) => {
                    e.preventDefault();
                    editDone({ content: e.target.input.value, id: id });
                  }}
                >
                  <input type="text" placeholder={content} name="input" />
                  <button type="submit">done</button>
                </form>
              ) : (
                <p>{content}</p>
              )}

              {!isEditing ? (
                <button onClick={() => edit(id)}>edit</button>
              ) : (
                <button onClick={() => (isEditing = !isEditing)}>cancel</button>
              )}
              <button onClick={() => remove(id)}>remove</button>

              <select
                value={type}
                onChange={(e) => changePlace({ id: id, type: e.target.value })}
              >
                <option> todo</option>
                <option>doing</option>
                <option>done</option>
              </select>

              <br />
            </div>
          );
        }
      })}
    </td>
  );
};
