import React, { useContext, useEffect, useReducer } from "react";
import reducer from "./reducer";

export const AppContext = React.createContext();
const initialState = {
  //the initial values
  text: [{ type: "", content: "", id: 0, isEditing: false }],
  coloumns: [],
};
export const AppProvider = ({ children }) => {
  //the functions and common values

  const [state, dispatch] = useReducer(reducer, initialState);

  const addTask = ({ text, type }) => {
    dispatch({ type: "ADD_TASK", text: text, func: type });
  };
  const remove = (id) => {
    dispatch({ type: "REMOVE", id: id });
  };
  const edit = (id) => {
    dispatch({ type: "EDIT", id: id });
  };
  const editDone = ({ id, content }) => {
    dispatch({ type: "EDIT-DONE", id: id, content: content });
  };
  const clean = () => {
    dispatch({ type: "CLEAN" });
  };
  const changePlace = ({ id, type }) => {
    dispatch({ type: "CHANGE-PLACE", id: id, newType: type });
  };
  const removeColoumn = (category) => {
    dispatch({ type: "DELETE-COLOUMN", category: category });
  };
  const addCol = (type) => {
    dispatch({ type: "ADD-COL", col: type });
  };
  const getLocalStorage = () => {
    let list = localStorage.getItem("tasks");
    console.log(typeof list);
    if (list) {
      // console.log(list);
      dispatch({ type: "LOAD", initialValue: list });
    } else {
      return [];
    }
  };
  useEffect(() => {
    // console.log(state.coloumns);
    localStorage.setItem("tasks", JSON.stringify(state));
  }, [state]);

  return (
    <AppContext.Provider
      value={{
        addTask,
        remove,
        edit,
        editDone,
        state,
        clean,
        changePlace,
        removeColoumn,
        addCol,
        getLocalStorage,
      }}
    >
      {children}
    </AppContext.Provider>
  );
};

export const useGlobalContext = () => {
  return useContext(AppContext);
};
