import React, { useContext, useEffect, useReducer, useState } from "react";
import reducer from "./reducer";

const AppContext = React.createContext();
const initialState = {
  //the initial values
  text: [{ type: "", content: "", id: 0, isEditing: false }],
};
const AppProvider = ({ children }) => {
  //the functions and common values

  const [isLoading, setIsLoading] = useState(true);
  const [state, dispatch] = useReducer(reducer, initialState);

  const addTask = (text, type) => {
    dispatch({ type: "ADD_TASK_TODO", text: text, func: type });
  };
  const remove = (id) => {
    dispatch({ type: "REMOVE", id: id });
  };
  const edit = (id) => {
    dispatch({ type: "EDIT", id: id });
  };

  useEffect(() => {}, [state.text]);
  return (
    <AppContext.Provider value={{ isLoading, ...state, addTask, remove, edit }}>
      {children}
    </AppContext.Provider>
  );
};

export const useGlobalContext = () => {
  return useContext(AppContext);
};
export default { AppProvider, AppContext };
