const reducer = (state, action) => {
  if (action.type === "ADD_TASK") {
    let newItem = {
      type: action.func,
      id: new Date().getTime().toString(),
      content: action.text,
    };

    return {
      ...state,
      text: [...state.text, newItem],
    };
  }

  if (action.type === "REMOVE") {
    return {
      ...state,
      text: state.text.filter((cartItem) => cartItem.id !== action.id),
    };
  }

  if (action.type === "EDIT") {
    let selectedItem = state.text.find((item) => item.id === action.id);
    selectedItem.isEditing = true;
    return { ...state };
  }
  if (action.type === "EDIT-DONE") {
    let selectedItem = state.text.find((item) => item.id === action.id);
    let editedValue = (selectedItem.content = action.content);
    return { ...state.text };
  }
  if (action.type === "GRAB ") {
    console.log("hg");
    return { ...state };
  }
};
export default reducer;
