import { useGlobalContext } from "./context";
export const Component = ({ name }) => {
  const { edit, addTask, remove, state, editDone, changePlace, removeColoumn } =
    useGlobalContext();
  console.log(name);

  return (
    <div>
      {state.text.map((task) => {
        const { type, content, id, isEditing, belongsTo } = task;
        if (type === name) {
          console.log(name);
          return (
            <div key={id}>
              <h3>{name}</h3>
              <form
                onSubmit={(e) => {
                  e.preventDefault();
                  addTask({ text: e.target.inputref.value, type: name });
                }}
              >
                <input type="text" name="inputref" />
                <button type="submit">Add</button>
                <button onClick={() => removeColoumn(name)}> Remove</button>
              </form>
              {isEditing ? (
                <form
                  onSubmit={(e) => {
                    e.preventDefault();
                    editDone({ content: e.target.input.value, id: id });
                  }}
                >
                  <input type="text" placeholder={content} name="input" />
                  <button type="submit">done</button>
                </form>
              ) : (
                <p>{content}</p>
              )}

              {!isEditing && <button onClick={() => edit(id)}>edit</button>}
              <button onClick={() => remove(id)}>remove</button>

              <select
                value={type}
                onChange={(e) => changePlace({ id: id, type: e.target.value })}
              >
                {state.coloumns.map((item) => {
                  return <option>{item}</option>;
                })}
              </select>
            </div>
          );
        }
        <hr />;
      })}
    </div>
  );
};
