import React, { useContext, useEffect, useReducer } from "react";
import { reducer } from "./reducer";

export const loader = ({ section, id }) => {
  console.log(id);
  const fetch = async () => {
    const response = await fetch(`http://localhost:1337/api/usersapis/${id}`);
    const initData = await response.json();

    const data = initData.attributes;
    if (!data.boards) {
      console.log("hi");
      return (
        (data.boards = [""]),
        (data.coloumns = [{ text: "", id: 0 }]),
        (data.text = [
          { type: "", content: "", id: 0, isEditing: false, belongTo: "" },
        ])((loadingInfo.logedIn = false))
      );
    }

    if (section === "coloumns") {
      return data.coloumns;
    }
    if (section === "text") {
      return data.text;
    }
    if (section === "boards") {
      return data.boards;
    }
    if (section === "log") {
      return loadingInfo.logedIn;
    }
  };
  fetch();
  const loadingInfo = JSON.parse(localStorage.getItem("tasks"));
  loadingInfo.id = id;
};

export const AppContext = React.createContext();
const initialState = {
  //the initial values
  boards: [""], //loader("boards"),
  text: [{ type: "", content: "", id: 0, isEditing: false, belongTo: "" }], // loader("text"),
  coloumns: [{ text: "", id: 0 }], // loader("coloumns"),
  logedIn: loader("log"),
  signingUp: false,
  id: 0,
};
export const AppProvider = ({ children }) => {
  //the functions and common values

  const [state, dispatch] = useReducer(reducer, initialState);

  const addTask = ({ text, model }) => {
    dispatch({ type: "ADD_TASK", text: text, func: model });
  };
  const remove = (id) => {
    dispatch({ type: "REMOVE", id: id });
  };
  const edit = (id) => {
    dispatch({ type: "EDIT", id: id });
  };
  const editDone = ({ id, content }) => {
    dispatch({ type: "EDIT-DONE", id: id, content: content });
  };
  const clean = () => {
    dispatch({ type: "CLEAN" });
  };
  const changePlace = ({ id, type }) => {
    dispatch({ type: "CHANGE-PLACE", id: id, newType: type });
  };
  const removeColoumn = (title) => {
    dispatch({ type: "DELETE-COLOUMN", category: title });
  };
  const addCol = ({ text, type }) => {
    dispatch({ type: "ADD-COL", text: text, model: type });
  };
  const addBoard = (text) => {
    dispatch({ type: "ADD-BOARD", text: text });
  };
  const logInFunc = (uname) => {
    dispatch({ type: "ENTER", uname: uname });
  };
  const exit = () => {
    dispatch({ type: "EXIT" });
  };
  const signingUp = () => {
    dispatch({ type: "SIGN-UP" });
  };
  const logIn = ({ uname, pass }) => {
    dispatch({ type: "LOG-IN", uname: uname, pass: pass });
  };
  const memberId = (mId) => {
    return { ...state, id: mId };
  };
  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(state));
    const memberData = {
      data: {
        border: state.boards,
        coloumns: state.coloumns,
        text: state.text,
      },
    };
    fetch(`http://localhost:1337/api/usersapis/${state.id}`, {
      method: "POST",
      mode: "cors",
      body: JSON.stringify(memberData),
    });
  }, [state]);

  return (
    <AppContext.Provider
      value={{
        addTask,
        remove,
        edit,
        editDone,
        state,
        clean,
        changePlace,
        removeColoumn,
        addCol,
        addBoard,
        logInFunc,
        exit,
        signingUp,
        logIn,
        memberId,
      }}
    >
      {children}
    </AppContext.Provider>
  );
};

export const useGlobalContext = () => {
  return useContext(AppContext);
};
