import { AllComponents } from "./ComponentContainer";
import { useGlobalContext } from "./context";
export const Board = () => {
  const { state, addCol } = useGlobalContext();
  return (
    <div>
      {state.boards.map((item, index) => {
        const { content, id } = item;
        return (
          <div>
            <hr />
            <ul>
              <li key={index}>
                <h3>Boarder:{content}</h3>
                <form
                  onSubmit={(e) => {
                    e.preventDefault();
                    addCol(e.target.input.value);
                  }}
                >
                  <label>Add Coloumn:</label>
                  <input name="input" type="text" />
                  <button type="submit">Add</button>
                </form>
              </li>
            </ul>
            <AllComponents name={content} />
          </div>
        );
      })}
    </div>
  );
};
