import { Navbar } from "./Navbar";
import { ComponentContainer } from "./ComponentContainer";
import { Board } from "./Board";
import { Route, Routes, Link } from "react-router-dom";
import { useGlobalContext } from "./context";
import { LogIn } from "./Log";
import "./index.css";
import { SignUp } from "./SignUp";
import { useParams, useOutletContext } from "react-router-dom";
function App() {
  const { addBoard, state } = useGlobalContext();
  const { id } = useParams();
  if (state.logedIn) {
    return (
      <main>
        {id}
        <Navbar />
        <form
          onSubmit={(e) => {
            e.preventDefault();
            addBoard(e.target.input.value);
          }}
        >
          <label>Add a board:</label>
          <input type="text" name="input" />
          <button type="submit" className="addbtn">
            Add Board
          </button>

          {state.logedIn ? (
            <Link to="/boards" className="boardpg">
              Boards Page
            </Link>
          ) : (
            <p>plz log in first</p>
          )}
        </form>
        <h5>Your Boards:</h5>
        <ol className="board-list">
          {state.boards.map((board) => {
            // const { id, content } = board;
            return (
              <>
                <li>
                  <p>{board}</p>
                </li>
              </>
            );
          })}
        </ol>
        <br />
        <Routes>
          <Route path="/boards" element={<Board />} />
          <Route path="/lists" element={<ComponentContainer />} />
          <Route path="/signup" element={<SignUp />} />;
        </Routes>
      </main>
    );
  }
  if (state.signingUp) {
    return <SignUp />;
  }
  return <LogIn />;
}

export default App;
