import { Component } from "./Component";
import { useGlobalContext } from "./context";
const AllComponents = () => {
  const { addCol, state } = useGlobalContext();
  return (
    <div>
      {state.coloumns.map((item) => (
        <Component name={item} />
      ))}
      <Component name="todo" /> <Component name="doing" />
      <Component name="done" />
      <form onSubmit={(e) => addCol(e.target.input.value)}>
        <label>Add Coloumn:</label>
        <input name="input" type="text" />
        <button type="submit">Add</button>
      </form>
    </div>
  );
};

export default AllComponents;
