import { Component } from "./Component";
import { useRef } from "react";
const AllComponents = () => {
  const inputRef = useRef();
  const add = () => {
    console.log(inputRef.current.value);
    return (
      <div>
        <h3>{inputRef.current.value}</h3>
        <Component name={inputRef.current.value} />
      </div>
    );
  };
  return (
    <div>
      <h3>to do</h3>
      <Component name="todo" />
      <h3>doing</h3>
      <Component name="doing" />
      <h3>done</h3>
      <Component name="done" />
      <label>Add Coloumn:</label>
      <input ref={inputRef} name="input" type="text" />
      <button onClick={add}>Add</button>

      {inputRef.current.value === false && <Component name="f" />}
    </div>
  );
};

export default AllComponents;
