import { useState } from "react";
import { useGlobalContext } from "./context";
const reducer = (state, action) => {
  if (action.type === "ADD_TASK_TODO") {
    let newItem = {
      type: action.func,
      id: new Date().getTime().toString(),
      content: action.text,
    };

    return {
      ...state,
      text: newItem,
    };
  }

  if (action.type === "REMOVE") {
    return {
      ...state,
      text: state.text.filter((cartItem) => cartItem.id !== action.id),
    };
  }

  if (action.type === "EDIT") {
  }
};
export default reducer;
