import { Component } from "./Component";
import { useGlobalContext } from "./context";
import { useRef } from "react";
const AllComponents = () => {
  const { addCol } = useGlobalContext();
  const inputRef = useRef();
  const add = () => {
    let col = inputRef.current.value;
    addCol(col);
  };
  return (
    <section>
      <h3>to do</h3>
      <Component name="todo" />
      <h3>doing</h3>
      <Component name="doing" />
      <h3>done</h3>

      <Component name="done" />
      {
        <>
          <label>Add Coloumn:</label>
          <input ref={inputRef} name="input" type="text" />
          <button
            onClick={() => {
              console.log("lk");
              // console.log(inputRef.current.value);
              return (
                <div>
                  <h3>{inputRef.current.value}</h3>
                  {/* <Component name={inputRef.current.value} /> */}
                </div>
              );
            }}
          >
            add
          </button>
        </>
      }
    </section>
  );
};

export default AllComponents;
