// const { edit, addTask, remove, state, editDone } = useGlobalContext();
import { useRef } from "react";
import { useGlobalContext } from "./context";
export const Component = () => {
  const { edit, addTask, remove, state, editDone, grabber } =
    useGlobalContext();
  const inputRef = useRef(null);
  return (
    <tr>
      <td>
        {/* todo */}
        <form
          onSubmit={(e) => {
            e.preventDefault();
            console.log(e.target);
            addTask({ text: inputRef.current.value, type: "todo" });
          }}
        >
          <input type="text" name="inputref" ref={inputRef} />
          <button type="submit">Add</button>
        </form>
        <br />

        {state.text.map((task) => {
          const { type, content, id, isEditing } = task;
          if (type === "todo") {
            return (
              <div key={id}>
                {isEditing ? (
                  <form
                    onSubmit={(e) => {
                      e.preventDefault();
                      editDone({ content: e.input.value, id: id });
                    }}
                  >
                    <input
                      type="text"
                      value={content}
                      name="input"
                      onChange={(e) =>
                        addTask({ text: e.target.value, type: "todo" })
                      }
                    />
                    <button type="submit">done</button>
                  </form>
                ) : (
                  <p>{content}</p>
                )}
                <button onClick={() => edit(id)}>edit</button>
                <button onClick={() => remove(id)}>remove</button>
                <br />
              </div>
            );
          }
        })}
      </td>
      {/* doing */}
      <td>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            addTask({ text: e.target.value, type: "doing" });
          }}
        >
          <input
            type="text"
            // ref={}
            // onChange={(e) => addTask({ text: e.target.value, type: "doing" })}
          />
          <button type="submit">Add</button>
        </form>
        <br />

        {state.text.map((task) => {
          const { type, content, id, isEditing } = task;
          if (type === "doing") {
            return (
              <div key={id}>
                {isEditing ? (
                  <div>
                    <form
                      onSubmit={(e) => {
                        e.preventDefault();
                        editDone({ content: e.target.value, id: id });
                      }}
                    >
                      <input
                        type="text"
                        value={content}
                        // ref={inputRef}
                        // onChange={(e) =>
                        //   addTask({ text: e.target.value, type: "doing" })
                        // }
                      />
                      <button type="submit">done</button>
                    </form>
                  </div>
                ) : (
                  <p>{content}</p>
                )}
                <button onClick={() => edit(id)}>edit</button>
                <button onClick={() => remove(id)}>remove</button>
                <br />
              </div>
            );
          }
        })}
      </td>
      <td>
        {/* todo */}
        <form
          onSubmit={(e) => {
            e.preventDefault();
            addTask({ text: inputRef.current.value, type: "done" });
          }}
        >
          <input
            type="text"
            ref={inputRef}
            onChange={(e) => addTask({ text: e.target.value, type: "done" })}
          />
          <button type="submit">Add</button>
        </form>
        <br />

        {state.text.map((task) => {
          const { type, content, id, isEditing } = task;
          if (type === "done") {
            return (
              <div key={id}>
                {isEditing ? (
                  <div>
                    <form
                      onSubmit={(e) =>
                        editDone({ content: e.target.value, id: id })
                      }
                    >
                      <input
                        type="text"
                        value={content}
                        onChange={(e) =>
                          addTask({ text: e.target.value, type: "done" })
                        }
                      />
                      <button type="submit">done</button>
                    </form>
                  </div>
                ) : (
                  <p>{content}</p>
                )}
                <button onClick={() => edit(id)}>edit</button>
                <button onClick={() => remove(id)}>remove</button>
                <br />
              </div>
            );
          }
        })}
      </td>
    </tr>
  );
};
