import { Component } from "./Component";
import { useRef } from "react";
const AllComponents = () => {
  const inputRef = useRef();
  const add = (e) => {
    e.preventDefault();
    console.log(inputRef.current.value);
    return (
      <div>
        <h3>{e.target.input.value}</h3>
        <Component name={e.target.input.value} />
      </div>
    );
  };
  return (
    <div>
      <h3>to do</h3>
      <Component name="todo" />
      <h3>doing</h3>
      <Component name="doing" />
      <h3>done</h3>
      <Component name="done" />
      <form onSubmit={() => add(e)}>
        <label>Add Coloumn:</label>
        <input ref={inputRef} name="input" type="text" />
        <button type="submit">Add</button>
      </form>
    </div>
  );
};

export default AllComponents;
