const reducer = (state, action) => {
  if (action.type === "DELETE-COLOUMN") {
    return {
      ...state,
      coloumns: state.coloumns.filter((item) => item !== action.category),
    };
  }
  if (action.type === "ADD_TASK") {
    let newItem = {
      type: action.func,
      id: new Date().getTime().toString(),
      content: action.text,
    };
    console.log(newItem);
    return {
      ...state,
      text: [...state.text, newItem],
    };
  }

  if (action.type === "REMOVE") {
    return {
      ...state,
      text: state.text.filter((item) => item.id !== action.id),
    };
  }
  if (action.type === "LOAD") {
    let loadingContent = localStorage.getItem("tasks");
    return { ...state, loadingContent };
  }

  if (action.type === "EDIT") {
    let selectedItem = state.text.find((item) => item.id === action.id);
    selectedItem.isEditing = true;
    return { ...state };
  }
  if (action.type === "EDIT-DONE") {
    state.text.map((item) => {
      if (item.id === action.id) {
        item.content = action.content;
        item.isEditing = false;
      }
    });
    return { ...state };
  }
  if (action.type === "CLEAN") {
    return { text: [], coloumns: [] };
  }
  if (action.type === "CHANGE-PLACE") {
    state.text.map((item) => {
      if (item.id === action.id) {
        item.type = action.newType;
      }
    });
    return { ...state };
  }
  if (action.type === "LOAD") {
    console.log("action.initialValue");
    return { ...state, ...action.initialValue };
  }
  if (action.type === "ADD-COL") {
    let findItem = state.coloumns.find((item) => item === action.col);
    let newItem = action.col;
    if (!findItem) {
      return { ...state, coloumns: [...state.coloumns, newItem] };
    } else {
      return { ...state };
    }
  }
};
export default reducer;
