import "./index.css";
import { useGlobalContext } from "./context";
import { useEffect } from "react";

export const LogIn = () => {
  const { state, logInFunc } = useGlobalContext();
  const fetchDataBase = async ({ uname, password }) => {
    const response = await fetch("http://localhost:1337/api/usersapis");
    const data = await response.json();
    console.log(data);
    data.attributes.find((member) => {
      const { username, pass } = member;
      if ((username === uname, pass === password)) {
        logInFunc(uname);
      }
      console.log("plz enter first");
    });
  };
  return (
    <div className="log">
      <form
        className="form"
        onSubmit={(e) => {
          e.preventDefault();
          fetchDataBase({
            uname: e.target.uname.value,
            password: e.target.psw.value,
          });
        }}
      >
        <div className="formdiv">
          <label htmlFor="uname">
            <b>Username</b>
          </label>
          <input type="text" placeholder="Enter Username" name="uname" />
          <br />

          <label htmlFor="psw">
            <b>Password</b>
          </label>
          <input type="password" placeholder="Enter Password" name="psw" />
          <br />
          <button type="submit" className="button">
            Login
          </button>
          <br />
          <button type="button" className="cancelbtn">
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};
